class Person {
 private int age;

 public int getAge() {
 return age;
 }

 public void setAge(int age) {
 this.age = age;
 }
}

class Main {
 public static void main(String[] args) {
 // create an object of Person
 Person p1 = new Person();
 p1.setAge(21);

 System.out.println("My age is " + p1.getAge());
 }
}

