public class SimpleArrayExample {
 public static void main(String[] args) {
 // Declare and initialize an array of integers
 int[] numbers = {10, 20, 30, 40, 50};

 // Accessing and printing elements of the array
 System.out.println("Elements of the array:");
 for (int i = 0; i < numbers.length; i++) {
 System.out.println("Element at index " + i + ": " + numbers[i]);
 }

 // Modifying an element of the array
 numbers[2] = 35;

 // Displaying the modified array
 System.out.println("\nModified array:");
 for (int number : numbers) {
 System.out.println(number);
 }

 // Summing up the elements of the array
 int sum = 0;
 for (int number : numbers) {
 sum += number;
 }

 System.out.println("\nSum of array elements: " + sum);
 }
}

public class StringExample {
 public static void main(String[] args) {
 // Declaration and initialization of strings
 String greeting = "Hello,";
 String name = "John";

 // Concatenation of strings
 String message = greeting + " " + name + "!";

 // Displaying the concatenated string
 System.out.println(message);

 // String length
 int length = message.length();
 System.out.println("Length of the message: " + length);

 // Accessing characters in a string
 char firstChar = message.charAt(0);
 char lastChar = message.charAt(message.length() - 1);
 System.out.println("First character: " + firstChar);
 System.out.println("Last character: " + lastChar);

 // Substring
 String subMessage = message.substring(7, 12);
 System.out.println("Substring: " + subMessage);

 // Uppercase and lowercase
 String upperCaseMessage = message.toUpperCase();
 String lowerCaseMessage = message.toLowerCase();
 System.out.println("Uppercase message: " + upperCaseMessage);
 System.out.println("Lowercase message: " + lowerCaseMessage);

 // String comparison
 String compareString1 = "Java";
 String compareString2 = "java";
 boolean isEqual = compareString1.equals(compareString2);
 System.out.println("String comparison: " + isEqual);
 }
}
